var App={init:function(){var i=parseInt($(".pins article").css("margin-bottom")),t=3;$(".pins").width()<768&&(t=2);for(var n=$(".pins").width()/t-i,p=[],o=0;t>o;o++)p[o]=0;$(".pins article").each(function(){var o=0;p.forEach(function(i,t){p[t]<p[o]&&(o=t)});var s=i*(o+1)+o*n;$(this).css({position:"absolute",width:n+"px",top:p[o]+"px",left:s+"px"}),p[o]+=$(this).height()+i,o=(o+1)%t})}};$(function(){App.init(),$(window).resize(function(){App.init()})});